// PlusPhasePost does special algorithm processing at end of plus
func (ly *Layer) PlusPhasePost(ctx *Context) {
	ly.PlusPhaseActAvg(ctx)
	ly.CorSimFromActs(ctx) // GPU syncs down the state before this
	np := ly.NPools
	if ly.Type == PTMaintLayer && ly.Name == "OFCposPT" {
		for pi := uint32(1); pi < np; pi++ {
			for di := uint32(0); di < ctx.NData; di++ {
				pl := ly.Pool(pi, di)
				val := pl.AvgMax.CaSpkD.Cycle.Avg
				GlobalVectors.Set(val, int(GvOFCposPTMaint), int(uint32(pi-1)), int(di))
			}
		}
	}

	if ly.Params.Acts.Decay.OnRew.IsTrue() {
		for di := uint32(0); di < ctx.NData; di++ {
			hasRew := (GlobalScalars.Value(int(GvHasRew), int(di)) > 0)
			giveUp := (GlobalScalars.Value(int(GvGiveUp), int(di)) > 0)
			if hasRew || giveUp {
				ly.DecayState(ctx, di, 1, 1, 1)      // note: GPU will get, and GBuf are auto-cleared in NewState
				for pi := uint32(0); pi < np; pi++ { // also clear the pool stats: GoalMaint depends on these..
					pl := ly.Pool(pi, di)
					pl.AvgMax.Zero()
				}
			}
		}
	}
	switch ly.Type {
	case MatrixLayer:
		ly.MatrixGated(ctx)
	}
}

// PlusPhaseActAvg updates ActAvg and DTrgAvg at the plus phase
// Note: could be done on GPU but not worth it at this point..
func (ly *Layer) PlusPhaseActAvg(ctx *Context) {
	nn := ly.NNeurons
	for lni := uint32(0); lni < nn; lni++ {
		ni := ly.NeurStIndex + lni
		if NrnIsOff(ni) {
			continue
		}
		dTrgSum := float32(0)
		avgSum := float32(0)
		for di := uint32(0); di < ctx.NData; di++ {
			dTrgSum += ly.Params.LearnTrgAvgErrLRate() * (Neurons.Value(int(CaSpkP), int(ni), int(di)) - Neurons.Value(int(CaSpkD), int(ni), int(di)))
			avgSum += ly.Params.Acts.Dt.LongAvgDt * (Neurons.Value(int(ActM), int(ni), int(di)) - NeuronAvgs.Value(int(ActAvg), int(ni)))
		}
		NeuronAvgs.SetAdd(dTrgSum, int(DTrgAvg), int(ni))
		NeuronAvgs.SetAdd(avgSum, int(ActAvg), int(ni))
	}
}

// TargToExt sets external input Ext from target values Target
// This is done at end of MinusPhase to allow targets to drive activity in plus phase.
// This can be called separately to simulate alpha cycles within theta cycles, for example.
func (ly *Layer) TargToExt(ctx *Context) {
	nn := ly.NNeurons
	for lni := uint32(0); lni < nn; lni++ {
		ni := ly.NeurStIndex + lni
		if NrnIsOff(ni) {
			continue
		}
		for di := uint32(0); di < ctx.NData; di++ {
			if !NrnHasFlag(ni, di, NeuronHasTarg) { // will be clamped in plus phase
				continue
			}
			Neurons.Set(Neurons.Value(int(Target), int(ni), int(di)), int(Ext), int(ni), int(di))
			NrnSetFlag(ni, di, NeuronHasExt)
			Neurons.Set(-1, int(ISI), int(ni), int(di)) // get fresh update on plus phase output acts
			Neurons.Set(-1, int(ISIAvg), int(ni), int(di))
		}
	}
}

// ClearTargExt clears external inputs Ext that were set from target values Target.
// This can be called to simulate alpha cycles within theta cycles, for example.
func (ly *Layer) ClearTargExt(ctx *Context) {
	nn := ly.NNeurons
	for lni := uint32(0); lni < nn; lni++ {
		ni := ly.NeurStIndex + lni
		if NrnIsOff(ni) {
			continue
		}
		for di := uint32(0); di < ctx.NData; di++ {
			if !NrnHasFlag(ni, di, NeuronHasTarg) { // will be clamped in plus phase
				continue
			}
			Neurons.Set(0, int(Ext), int(ni), int(di))
			NrnClearFlag(ni, di, NeuronHasExt)
			Neurons.Set(-1, int(ISI), int(ni), int(di)) // get fresh update on plus phase output acts
			Neurons.Set(-1, int(ISIAvg), int(ni), int(di))
		}
	}
}

